mapper.select:
SELECT ebsc.* FROM erp_bank_slip_claim ebsc
INNER JOIN(
SELECT Cebsc.other_side_account_no,MAX(Cebsc.claim_serial_no) claim_serial_no FROM(
SELECT Bebsc.other_side_account_no,Bebsc.claim_serial_no FROM (
SELECT claim_serial_no,COUNT(1) line FROM erp_bank_slip_claim WHERE data_status = 1 AND recharge_status = 2 GROUP BY claim_serial_no
)Aebsc
INNER JOIN erp_bank_slip_claim Bebsc ON Bebsc.claim_serial_no = Aebsc.claim_serial_no AND Aebsc.line = 1 AND Bebsc.other_side_account_no != ''
)Cebsc GROUP BY Cebsc.other_side_account_no
)Debsc ON Debsc.claim_serial_no = ebsc.claim_serial_no

SELECT ebsc.payer_name,cc.company_name,cc.customer_no FROM ( SELECT MAX(bsc.id) AS id,bsd.payer_name FROM (SELECT Bebsc.* FROM ( -- 查询出只有被一个人认领的记录 SELECT claim_serial_no, COUNT(1) line FROM erp_bank_slip_claim WHERE data_status = 1 AND recharge_status = 2 GROUP BY claim_serial_no HAVING COUNT(1)=1) AS Aebsc INNER JOIN erp_bank_slip_claim AS Bebsc ON Bebsc.claim_serial_no = Aebsc.claim_serial_no AND Bebsc.other_side_account_no != '' ) AS bsc INNER JOIN erp_bank_slip_detail AS bsd ON bsc.bank_slip_detail_id = bsd.id WHERE bsd.payer_name IN
#{item.simpleCompanyName,jdbcType=VARCHAR}
GROUP BY bsd.payer_name ) ebsc INNER JOIN erp_bank_slip_claim AS bsc1 ON bsc1.id=ebsc.id INNER JOIN erp_customer_company AS cc ON cc.customer_no = bsc1.customer_no

