SELECT ,,ec.customer_name as customerName,ek3mc.k3_customer_code AS k3CustomerNo ,esc1.sub_company_name AS subCompanyName,ebs.sub_company_id AS ownerSubCompanyId,esc.sub_company_name AS ownerSubCompanyName,ebs.bank_type AS bankSlipBankType  FROM erp_bank_slip_detail ebsd
LEFT JOIN erp_bank_slip_claim ebsc ON ebsc.bank_slip_detail_id = ebsd.id AND ebsc.data_status = 1
LEFT JOIN erp_customer ec ON ebsc.customer_no = ec.customer_no AND ec.data_status = 1
LEFT JOIN erp_k3_mapping_customer ek3mc ON ek3mc.erp_customer_code = ec.customer_no
INNER JOIN erp_bank_slip ebs ON ebsd.bank_slip_id = ebs.id AND ebs.data_status = 1
LEFT JOIN  erp_sub_company esc ON esc.id = ebs.sub_company_id AND esc.data_status = 1
LEFT JOIN  erp_sub_company esc1 ON esc1.id = ebsd.sub_company_id AND esc1.data_status = 1
WHERE ebsd.id = #{maps.bankSlipDetailId, jdbcType=INTEGER} AND ebsd.data_status = 1

	and 1=2


	and 1=1


	and ebs.slip_status !=0 AND ebsd.detail_status != 5


	AND (ebsd.detail_status = 1 OR ebsd.detail_status = 2 or (CASE WHEN ebsd.detail_status = 3 AND(
	ec.OWNER = #{maps.currentUser,jdbcType=VARCHAR}
	or ec.union_user = #{maps.currentUser,jdbcType=VARCHAR}
	or ebsc.update_user = #{maps.currentUser,jdbcType=VARCHAR}
	or ebsc.create_user = #{maps.currentUser,jdbcType=VARCHAR}) THEN 1 = 1 ELSE 1=2 END ))


	and (ebs.sub_company_id = #{maps.subCompanyId,jdbcType=INTEGER} OR (ebsd.sub_company_id = #{maps.subCompanyId,jdbcType=INTEGER} AND ebsd.is_localization = 1 AND (ebs.slip_status = 1 OR ebs.slip_status = 2) AND ebsd.detail_status != 5) OR
	(CASE WHEN ebsd.detail_status = 3 AND(
	ec.OWNER = #{maps.currentUser,jdbcType=VARCHAR}
	or ec.union_user = #{maps.currentUser,jdbcType=VARCHAR}
	or ebsc.update_user = #{maps.currentUser,jdbcType=VARCHAR}
	or ebsc.create_user = #{maps.currentUser,jdbcType=VARCHAR}) THEN 1 = 1 ELSE 1=2 END )
	)

SELECT  FROM erp_bank_slip_detail ebsd


		AND ebsd.data_status = 1


LIMIT #{maps.start},#{maps.pageSize}

SELECT  COUNT(ebsd.id) FROM erp_bank_slip_detail ebsd
