		or ebsc.update_user = #{maps.currentUser,jdbcType=VARCHAR}
		or ebsc.create_user = #{maps.currentUser,jdbcType=VARCHAR})))

	and ebsd.loan_sign = 1

SELECT result.*,escLocalization.sub_company_name AS subCompanyName,escOwner.sub_company_name AS ownerSubCompanyName,ek3mc.k3_customer_code AS k3CustomerNo FROM (
	SELECT  ,ebs.sub_company_id AS ownerSubCompanyId,ebs.bank_type AS bankSlipBankType,,ec.customer_name AS customerName FROM erp_bank_slip_detail ebsd
	INNER JOIN erp_bank_slip ebs ON ebsd.bank_slip_id = ebs.id AND ebs.data_status = 1
	LEFT JOIN  erp_bank_slip_claim ebsc ON ebsd.id = ebsc.bank_slip_detail_id AND ebsc.data_status = 1
	LEFT JOIN  erp_customer ec ON ebsc.customer_no = ec.customer_no


			and ebsd.payer_name like CONCAT('%','${maps.bankSlipDetailQueryParam.payerName}','%' )


			and ebsd.loan_sign = #{maps.bankSlipDetailQueryParam.loanSign,jdbcType=INTEGER}


			and ebsd.detail_status = #{maps.bankSlipDetailQueryParam.detailStatus,jdbcType=INTEGER}


			and ebsd.bank_slip_id = #{maps.bankSlipDetailQueryParam.bankSlipId,jdbcType=INTEGER}


			and ebsd.trade_serial_no = #{maps.bankSlipDetailQueryParam.tradeSerialNo,jdbcType=VARCHAR}


			and ebsd.is_localization = #{maps.bankSlipDetailQueryParam.isLocalization,jdbcType=INTEGER}


			 and ebs.slip_day >= #{maps.bankSlipDetailQueryParam.slipDayStart,jdbcType=TIMESTAMP} 


			 and ebs.slip_day < #{maps.bankSlipDetailQueryParam.slipDayEnd,jdbcType=TIMESTAMP} 


			and ebsd.data_status = 1


			and 1=2


			and 1=1


			and ebs.slip_status !=0 AND ebsd.detail_status != 5


			and ebs.slip_status !=0 AND ebsd.detail_status != 5
			AND (ebsd.detail_status = 1 OR ebsd.detail_status = 2 OR
			( ebsd.detail_status = 3 and (ec.OWNER = #{maps.currentUser,jdbcType=VARCHAR} or ec.union_user = #{maps.currentUser,jdbcType=VARCHAR}
			or ebsc.update_user = #{maps.currentUser,jdbcType=VARCHAR}
			or ebsc.create_user = #{maps.currentUser,jdbcType=VARCHAR})))


			and (ebs.sub_company_id = #{maps.subCompanyId,jdbcType=INTEGER} OR (ebsd.sub_company_id = #{maps.subCompanyId,jdbcType=INTEGER} AND ebsd.is_localization = 1 AND (ebs.slip_status = 1 OR ebs.slip_status = 2) AND ebsd.detail_status != 5) OR
			( ebsd.detail_status = 3 and (ec.OWNER = #{maps.currentUser,jdbcType=VARCHAR} or ec.union_user = #{maps.currentUser,jdbcType=VARCHAR}
			or ebsc.update_user = #{maps.currentUser,jdbcType=VARCHAR}
			or ebsc.create_user = #{maps.currentUser,jdbcType=VARCHAR})))

		and ebsd.loan_sign = 1

	ORDER BY ebsd.detail_status ASC ,ebsd.payer_name DESC
	LIMIT #{maps.start},#{maps.pageSize}
)result
LEFT JOIN  erp_sub_company escOwner ON escOwner.id = result.ownerSubCompanyId AND escOwner.data_status = 1
LEFT JOIN  erp_sub_company escLocalization ON escLocalization.id = result.sub_company_id AND escLocalization.data_status = 1
LEFT JOIN 	erp_k3_mapping_customer ek3mc ON ek3mc.erp_customer_code = result.claim_customer_no

