mapper.select:
SELECT  FROM erp_bank_slip_detail ebsd


		AND ebsd.data_status = 1


LIMIT #{maps.start},#{maps.pageSize}

SELECT COUNT(ebsd.id)  FROM erp_bank_slip_detail ebsd
INNER JOIN erp_bank_slip ebs ON ebsd.bank_slip_id = ebs.id AND ebs.data_status = 1


		and ebsd.payer_name like CONCAT('%','${maps.bankSlipDetailQueryParam.payerName}','%' )


		and ebsd.loan_sign = #{maps.bankSlipDetailQueryParam.loanSign,jdbcType=INTEGER}


		and ebsd.detail_status = #{maps.bankSlipDetailQueryParam.detailStatus,jdbcType=INTEGER}


		and ebsd.trade_serial_no = #{maps.bankSlipDetailQueryParam.tradeSerialNo,jdbcType=VARCHAR}


		and ebsd.is_localization = #{maps.bankSlipDetailQueryParam.isLocalization,jdbcType=INTEGER}


		and ebs.slip_month = #{maps.bankSlipDetailQueryParam.slipMonth,jdbcType=TIMESTAMP}


		and ebsd.data_status = 1


		and 1=2


		and 1=1


		and ebs.slip_status !=0 AND ebsd.detail_status != 5


		and (ebs.sub_company_id = #{maps.subCompanyId,jdbcType=INTEGER} OR (ebsd.sub_company_id = #{maps.subCompanyId,jdbcType=INTEGER} AND ebsd.is_localization = 1 AND (ebs.slip_status = 1 OR ebs.slip_status = 2) AND ebsd.detail_status != 5))

SELECT ,esc1.sub_company_name AS subCompanyName,ebs.sub_company_id AS ownerSubCompanyId,esc.sub_company_name AS ownerSubCompanyName,ebs.bank_type AS bankSlipBankType,,ek3mc.k3_customer_code AS k3CustomerNo,ec.customer_name AS customerName FROM erp_bank_slip_detail ebsd
INNER JOIN erp_bank_slip ebs ON ebsd.bank_slip_id = ebs.id AND ebs.data_status = 1
LEFT JOIN  erp_sub_company esc ON esc.id = ebs.sub_company_id AND esc.data_status = 1
LEFT JOIN  erp_sub_company esc1 ON esc1.id = ebsd.sub_company_id AND esc1.data_status = 1
LEFT JOIN  erp_bank_slip_claim ebsc ON ebsd.id = ebsc.bank_slip_detail_id AND ebsc.data_status = 1
LEFT JOIN  erp_k3_mapping_customer ek3mc ON ek3mc.erp_customer_code = ebsc.customer_no
LEFT JOIN  erp_customer ec ON ebsc.customer_no = ec.customer_no


		and ebsd.payer_name like CONCAT('%','${maps.bankSlipDetailQueryParam.payerName}','%' )


		and ebsd.loan_sign = #{maps.bankSlipDetailQueryParam.loanSign,jdbcType=INTEGER}


		and ebsd.detail_status = #{maps.bankSlipDetailQueryParam.detailStatus,jdbcType=INTEGER}


		and ebsd.trade_serial_no = #{maps.bankSlipDetailQueryParam.tradeSerialNo,jdbcType=VARCHAR}


		and ebsd.is_localization = #{maps.bankSlipDetailQueryParam.isLocalization,jdbcType=INTEGER}


		and ebs.slip_month = #{maps.bankSlipDetailQueryParam.slipMonth,jdbcType=TIMESTAMP}


		and ebsd.data_status = 1


		and 1=2


		and 1=1


		and ebs.slip_status !=0 AND ebsd.detail_status != 5


		and (ebs.sub_company_id = #{maps.subCompanyId,jdbcType=INTEGER} OR (ebsd.sub_company_id = #{maps.subCompanyId,jdbcType=INTEGER} AND ebsd.is_localization = 1 AND (ebs.slip_status = 1 OR ebs.slip_status = 2) AND ebsd.detail_status != 5))


ORDER BY ebsd.detail_status ASC ,ebsd.payer_name DESC
LIMIT #{maps.start},#{maps.pageSize}

