mapper.select:
select , tuc.real_name as create_user, tuu.real_name as update_user from erp_data_dictionary td left join erp_user tuc on tuc.id = td.create_user left join erp_user tuu on tuu.id = td.update_user
and td.id = #{dictionaryId, jdbcType=INTEGER}
and td.data_status != 2

select , tuc.real_name as create_user, tuu.real_name as update_user from erp_data_dictionary td left join erp_user tuc on tuc.id = td.create_user left join erp_user tuu on tuu.id = td.update_user
and (td.id = #{maps.dictionaryId, jdbcType=INTEGER} or td.parent_dictionary_id = #{maps.dictionaryId, jdbcType=INTEGER})
and td.data_status = #{maps.dataStatus, jdbcType=INTEGER}
and td.data_name like CONCAT('%','${maps.dataName}','%' )
and td.data_type = #{maps.dataType, jdbcType=INTEGER}
and td.data_status != 2
order by id,data_order,create_time limit #{maps.start},#{maps.pageSize}

select count(1) from erp_data_dictionary td
and (td.id = #{maps.dictionaryId, jdbcType=INTEGER} or td.parent_dictionary_id = #{maps.dictionaryId, jdbcType=INTEGER})
and td.data_status = #{maps.dataStatus, jdbcType=INTEGER}
and td.data_name like CONCAT('%','${maps.dataName}','%' )
and td.data_type = #{maps.dataType, jdbcType=INTEGER}
and td.data_status != 2
