mapper.select:
SELECT count(em.id) FROM erp_material em


		and em.material_type = #{maps.materialQueryParam.materialType, jdbcType=INTEGER}


		and em.is_main_material = #{maps.materialQueryParam.isMainMaterial, jdbcType=INTEGER}


		and em.material_model_id = #{maps.materialQueryParam.materialModelId, jdbcType=INTEGER}


		and em.is_rent = #{maps.materialQueryParam.isRent, jdbcType=INTEGER}


		and em.material_capacity_value = #{maps.materialQueryParam.materialCapacityValue, jdbcType=DOUBLE}


		and em.material_model like CONCAT('%','${maps.materialQueryParam.materialMode}','%' )


		and em.material_no like CONCAT('%','${maps.materialQueryParam.materialNo}','%' )


		and em.material_name like CONCAT('%','${maps.materialQueryParam.materialName}','%' )


		and em.brand_id = #{maps.materialQueryParam.brandId, jdbcType=INTEGER}


		 AND em.create_time >= #{maps.materialQueryParam.createStartTime} 


		 AND em.create_time <= #{maps.materialQueryParam.createEndTime} 


		AND em.data_status = 1

SELECT material_main.*,  FROM
(
	SELECT 

		emm.model_name AS material_model_name, eb.brand_name as brand_name

	FROM erp_material em
	LEFT JOIN erp_material_model emm ON em.material_model_id = emm.id
	LEFT JOIN erp_brand eb ON eb.id = em.brand_id


			and em.material_type = #{maps.materialQueryParam.materialType, jdbcType=INTEGER}


			and em.is_main_material = #{maps.materialQueryParam.isMainMaterial, jdbcType=INTEGER}


			and em.material_model_id = #{maps.materialQueryParam.materialModelId, jdbcType=INTEGER}


			and em.is_rent = #{maps.materialQueryParam.isRent, jdbcType=INTEGER}


			and em.material_capacity_value = #{maps.materialQueryParam.materialCapacityValue, jdbcType=DOUBLE}


			and em.material_no like CONCAT('%','${maps.materialQueryParam.materialNo}','%' )


			and em.material_name like CONCAT('%','${maps.materialQueryParam.materialName}','%' )


			and em.material_model like CONCAT('%','${maps.materialQueryParam.materialMode}','%' )


			and em.brand_id = #{maps.materialQueryParam.brandId, jdbcType=INTEGER}


			 AND em.create_time >= #{maps.materialQueryParam.createStartTime} 


			 AND em.create_time <= #{maps.materialQueryParam.createEndTime} 


			AND em.data_status = 1


	LIMIT #{maps.start},#{maps.pageSize}
) material_main LEFT JOIN erp_material_img emi ON material_main.id = emi.material_id AND emi.data_status = 1
ORDER BY material_main.id DESC

select ,,ebm.rentCount AS rent_count, ebm.rentCount-IFNULL(ero.returnCount,0)-IFNULL(eco.returnCount,0) as can_process_count from ( select ebm.material_id as id,count(1) rentCount from erp_bulk_material ebm left join erp_order eo on eo.order_no = ebm.order_no and eo.data_status = 1 where ebm.order_no is not null and ebm.order_no !="" and ebm.data_status = 1 and eo.buyer_customer_id=#{maps.customerId}
AND ebm.current_equipment_id IS NULL
group by ebm.material_id ) ebm left join ( SELECT erom.return_material_id AS material_id , SUM(return_material_count-real_return_material_count) AS returnCount FROM erp_return_order ero LEFT JOIN erp_return_order_material erom ON ero.id=erom.return_order_id AND erom.data_status = 1 WHERE ero.customer_id = #{maps.customerId} AND ero.data_status = 1 AND ero.return_order_status !=16 AND ero.return_order_status !=20 GROUP BY erom.return_material_id ) ero on ero.material_id = ebm.id
LEFT JOIN ( select ecom.src_change_material_id as material_id , sum(change_material_count-real_change_material_count) as returnCount from erp_change_order eco left join erp_change_order_material ecom on eco.id=ecom.change_order_id and ecom.data_status = 1 where eco.customer_id = #{maps.customerId} and eco.data_status = 1 and eco.change_order_status !=24 and eco.change_order_status !=28 group by ecom.src_change_material_id ) eco on eco.material_id = ebm.id
LEFT JOIN erp_material em ON em.id = ebm.id LEFT JOIN erp_material_img emi ON ebm.id = emi.material_id AND emi.data_status LIMIT #{maps.start},#{maps.pageSize}
