mapper.update:
update erp_order
SET

    order_status =

        when #{orderDO.id} then #{orderDO.orderStatus}



    actual_return_time =

        when #{orderDO.id} then #{orderDO.actualReturnTime}


WHERE id in

    #{orderDO.id,jdbcType=INTEGER}

