SELECT count(eo.id)
FROM erp_order eo
INNER JOIN erp_customer ec ON ec.id = eo.buyer_customer_id AND ec.data_status = 1


        and eo.order_no like CONCAT('%','${maps.orderForReturnQueryParam.orderNo}','%' )


        and ec.customer_no = #{maps.orderForReturnQueryParam.customerNo, jdbcType=VARCHAR}


        and eo.order_status IN (16,20,22)


         AND eo.create_time >= #{maps.orderForReturnQueryParam.createStartTime} 


         AND eo.create_time <= #{maps.orderForReturnQueryParam.createEndTime} 


        and eo.data_status = 1

SELECT
eap.province_name AS province_name,eac.city_name AS city_name,ead.district_name AS district_name, ec.customer_no AS buyer_customer_no
FROM erp_order eo INNER JOIN erp_order_consign_info eoci ON eo.id = eoci.order_id and eoci.data_status = 1 LEFT JOIN erp_area_province eap ON eap.id = eoci.province LEFT JOIN erp_area_city eac ON eac.id = eoci.city LEFT JOIN erp_area_district ead ON ead.id = eoci.district INNER JOIN erp_customer ec ON ec.id = eo.buyer_customer_id AND ec.data_status = 1
and eo.order_no like CONCAT('%','${maps.orderForReturnQueryParam.orderNo}','%' )
and ec.customer_no = #{maps.orderForReturnQueryParam.customerNo, jdbcType=VARCHAR}
and eo.order_status IN (16,20,22)
AND eo.create_time >= #{maps.orderForReturnQueryParam.createStartTime}
AND eo.create_time <= #{maps.orderForReturnQueryParam.createEndTime}
and eo.data_status = 1
ORDER BY eo.create_time DESC LIMIT #{maps.start},#{maps.pageSize}
