mapper.select:
select  from erp_product_category_property epcp


        and epcp.data_status = 1


LIMIT #{maps.start},#{maps.pageSize}

SELECT count(epcp.id) FROM erp_product_category_property epcp


        and epcp.category_id = #{maps.productCategoryPropertyPageParam.categoryId, jdbcType=INTEGER}


      AND epcp.data_status = 1

SELECT ,epc.category_name,emt.material_type_name FROM erp_product_category_property epcp LEFT JOIN erp_product_category epc ON epc.id = epcp.category_id AND epc.data_status = 1 LEFT JOIN erp_material_type emt ON emt.id = epcp.material_type AND emt.data_status = 1
and epcp.category_id = #{maps.productCategoryPropertyPageParam.categoryId, jdbcType=INTEGER}
AND epcp.data_status = 1
ORDER BY epcp.create_time DESC LIMIT #{maps.start},#{maps.pageSize};
