mapper.select:
SELECT epe.sku_id id,COUNT(1) rent_count ,COUNT(1)-SUM(CASE ero.return_order_status WHEN 1 OR 5 THEN IFNULL(erop.return_product_sku_count,0) WHEN 9 THEN IFNULL(erop.real_return_product_sku_count,0) ELSE 0 END ) can_return_count FROM erp_product_equipment epe INNER JOIN erp_order eo ON epe.order_no = eo.order_no AND eo.data_status=1 LEFT JOIN erp_return_order_product erop ON erop.return_product_sku_id = epe.sku_id AND erop.data_status=1 LEFT JOIN erp_return_order ero ON ero.id = erop.return_order_id AND ero.data_status=1 WHERE epe.order_no IS NOT NULL AND epe.order_no != ""
AND eo.buyer_customer_id = #{maps.customerId}
GROUP BY epe.sku_id LIMIT #{maps.start},#{maps.pageSize}
