mapper.select:
select  from erp_purchase_delivery_order epdo 


		and epdo.data_status = 1


LIMIT #{maps.start},#{maps.pageSize}

SELECT count(id)
FROM erp_purchase_delivery_order epdo


		and epdo.purchase_order_id = #{maps.queryParam.purchaseOrderId, jdbcType=INTEGER}


		and epdo.warehouse_id = #{maps.queryParam.warehouseId, jdbcType=INTEGER}


		and epdo.is_invoice = #{maps.queryParam.isInvoice, jdbcType=INTEGER}


		and epdo.is_new = #{maps.queryParam.isNew, jdbcType=INTEGER}


		and epdo.purchase_delivery_order_status = #{maps.queryParam.purchaseDeliveryOrderStatus, jdbcType=INTEGER}


		 AND epdo.create_time >= #{maps.queryParam.createStartTime} 


		 AND epdo.create_time <= #{maps.queryParam.createEndTime} 


		and epdo.data_status = 1

select ,,,es1.supplier_name,epo.purchase_no purchase_order_no from (
SELECT 
FROM erp_purchase_delivery_order epdo


		and epdo.purchase_order_id = #{maps.queryParam.purchaseOrderId, jdbcType=INTEGER}


		and epdo.warehouse_id = #{maps.queryParam.warehouseId, jdbcType=INTEGER}


		and epdo.is_invoice = #{maps.queryParam.isInvoice, jdbcType=INTEGER}


		and epdo.is_new = #{maps.queryParam.isNew, jdbcType=INTEGER}


		and epdo.purchase_delivery_order_status = #{maps.queryParam.purchaseDeliveryOrderStatus, jdbcType=INTEGER}


		 AND epdo.create_time >= #{maps.queryParam.createStartTime} 


		 AND epdo.create_time <= #{maps.queryParam.createEndTime} 


		and epdo.data_status = 1


ORDER BY epdo.create_time DESC
LIMIT #{maps.start},#{maps.pageSize}
) epdo
left join erp_purchase_delivery_order_product epdop on epdo.id = epdop.purchase_delivery_order_id and epdop.data_status=1
left join erp_purchase_delivery_order_material epdom on epdo.id = epdom.purchase_delivery_order_id and epdom.data_status=1
left join erp_supplier es1 on epdo.owner_supplier_id = es1.id
left join erp_purchase_order epo on epo.id=epdo.purchase_order_id
