mapper.select:
SELECT  FROM erp_purchase_receive_order epro 


		AND epro.data_status = 1


LIMIT #{maps.start},#{maps.pageSize}

SELECT COUNT(id)
FROM erp_purchase_receive_order epro


		AND epro.purchase_receive_no = #{maps.queryParam.purchaseReceiveNo, jdbcType=VARCHAR}


		AND epro.purchase_order_id = #{maps.queryParam.purchaseOrderId, jdbcType=INTEGER}


		AND epro.purchase_delivery_order_id = #{maps.queryParam.purchaseDeliveryOrderId, jdbcType=INTEGER}


		AND epro.warehouse_id = #{maps.queryParam.warehouseId, jdbcType=INTEGER}


		AND epro.product_supplier_id = #{maps.queryParam.productSupplierId, jdbcType=INTEGER}


		AND epro.auto_allot_status = #{maps.queryParam.autoAllotStatus, jdbcType=INTEGER}


		AND epro.is_invoice = #{maps.queryParam.isInvoice, jdbcType=INTEGER}


		AND epro.is_new = #{maps.queryParam.isNew, jdbcType=INTEGER}


		AND epro.purchase_receive_order_status = #{maps.queryParam.purchaseReceiveOrderStatus, jdbcType=INTEGER}


		 AND epro.create_time >= #{maps.queryParam.createStartTime} 


		 AND epro.create_time <= #{maps.queryParam.createEndTime} 


		 AND epro.confirm_time >= #{maps.queryParam.confirmStartTime} 


		 AND epro.confirm_time <= #{maps.queryParam.confirmEndTime} 


		AND epro.data_status = 1

SELECT , ,eu.real_name AS ownerName,es1.supplier_name AS productSupplierName
FROM (
SELECT 
FROM erp_purchase_receive_order epro


		AND epro.purchase_receive_no = #{maps.queryParam.purchaseReceiveNo, jdbcType=VARCHAR}


		AND epro.purchase_order_id = #{maps.queryParam.purchaseOrderId, jdbcType=INTEGER}


		AND epro.purchase_delivery_order_id = #{maps.queryParam.purchaseDeliveryOrderId, jdbcType=INTEGER}


		AND epro.warehouse_id = #{maps.queryParam.warehouseId, jdbcType=INTEGER}


		AND epro.product_supplier_id = #{maps.queryParam.productSupplierId, jdbcType=INTEGER}


		AND epro.auto_allot_status = #{maps.queryParam.autoAllotStatus, jdbcType=INTEGER}


		AND epro.is_invoice = #{maps.queryParam.isInvoice, jdbcType=INTEGER}


		AND epro.is_new = #{maps.queryParam.isNew, jdbcType=INTEGER}


		AND epro.purchase_receive_order_status = #{maps.queryParam.purchaseReceiveOrderStatus, jdbcType=INTEGER}


		 AND epro.create_time >= #{maps.queryParam.createStartTime} 


		 AND epro.create_time <= #{maps.queryParam.createEndTime} 


		 AND epro.confirm_time >= #{maps.queryParam.confirmStartTime} 


		 AND epro.confirm_time <= #{maps.queryParam.confirmEndTime} 


		AND epro.data_status = 1


LIMIT #{maps.start},#{maps.pageSize}
) epro LEFT JOIN erp_purchase_receive_order_product eprop ON eprop.purchase_receive_order_id = epro.id AND eprop.data_status=1
LEFT JOIN erp_user eu ON epro.owner = eu.id
LEFT JOIN erp_supplier es1 ON epro.product_supplier_id = es1.id
