SELECT count(esod.id) FROM erp_statement_order_detail esod LEFT JOIN erp_order_product eop ON esod.order_item_refer_id = eop.id AND esod.order_item_type = 1 LEFT JOIN erp_order_material eom ON esod.order_item_refer_id = eom.id AND esod.order_item_type = 2 LEFT JOIN erp_customer ec ON esod.customer_id = ec.id LEFT JOIN erp_order eo on eo.id = esod.order_id and esod.order_type = 1
and (eop.rent_length_type = #{maps.statementOrderDetailQueryParam.rentLengthType, jdbcType=INTEGER} OR eom.rent_length_type = #{maps.statementOrderDetailQueryParam.rentLengthType, jdbcType=INTEGER} OR eo.rent_length_type = #{maps.statementOrderDetailQueryParam.rentLengthType, jdbcType=INTEGER})
and esod.customer_id = #{maps.statementOrderDetailQueryParam.customerId, jdbcType=INTEGER}
and eo.order_sub_company_id = #{maps.statementOrderDetailQueryParam.subCompanyId, jdbcType=INTEGER}
and esod.statement_detail_status = 0
and esod.statement_detail_overdue_amount > 0
AND eso.create_time >= #{maps.statementOrderQueryParam.createStartTime}
AND eso.create_time <= #{maps.statementOrderQueryParam.createEndTime}
AND esod.data_status = 1

SELECT
FROM erp_statement_order_detail esod LEFT JOIN erp_order_product eop ON esod.order_item_refer_id = eop.id AND esod.order_item_type = 1 LEFT JOIN erp_order_material eom ON esod.order_item_refer_id = eom.id AND esod.order_item_type = 2 LEFT JOIN erp_customer ec ON esod.customer_id = ec.id LEFT JOIN erp_order eo on eo.id = esod.order_id and esod.order_type = 1
and (eop.rent_length_type = #{maps.statementOrderDetailQueryParam.rentLengthType, jdbcType=INTEGER} OR eom.rent_length_type = #{maps.statementOrderDetailQueryParam.rentLengthType, jdbcType=INTEGER} OR eo.rent_length_type = #{maps.statementOrderDetailQueryParam.rentLengthType, jdbcType=INTEGER})
and esod.customer_id = #{maps.statementOrderDetailQueryParam.customerId, jdbcType=INTEGER}
and eo.order_sub_company_id = #{maps.statementOrderDetailQueryParam.subCompanyId, jdbcType=INTEGER}
and esod.statement_detail_status = 0
and esod.statement_detail_overdue_amount > 0
AND eso.create_time >= #{maps.statementOrderQueryParam.createStartTime}
AND eso.create_time <= #{maps.statementOrderQueryParam.createEndTime}
AND esod.data_status = 1
LIMIT #{maps.start},#{maps.pageSize}

SELECT, eo3.rent_type, (CASE WHEN esod.order_item_type = 1 THEN eop.product_count WHEN esod.order_item_type = 2 THEN eom.material_count end) goods_count, (CASE WHEN esod.order_item_type = 1 THEN eop.product_unit_amount WHEN esod.order_item_type = 2 THEN eom.material_unit_amount end) goods_unit_amount FROM erp_statement_order_detail esod LEFT JOIN erp_order_product eop ON eop.id = esod.order_item_refer_id AND eop.data_status=1 AND esod.order_item_type = 1 LEFT JOIN erp_order_material eom ON eom.id = esod.order_item_refer_id AND eom.data_status=1 AND esod.order_item_type = 2 LEFT JOIN erp_customer ec ON esod.customer_id = ec.id LEFT JOIN erp_order eo1 ON eop.order_id = eo1.id AND eo1.data_status=1 LEFT JOIN erp_order eo2 ON eom.order_id = eo2.id AND eo2.data_status=1 LEFT JOIN erp_order eo3 ON esod.order_id = eo3.id AND eo3.data_status=1 LEFT JOIN erp_user eu ON eu.id=eo3.order_seller_id WHERE esod.data_status = 1 AND esod.order_type = 1 AND esod.statement_detail_status=8
AND eo3.rent_length_type = #{maps.incomeQueryParam.rentLengthType}
AND ec.customer_name LIKE CONCAT('%','${maps.incomeQueryParam.customerName}','%' )
AND eo3.order_sub_company_id = #{maps.incomeQueryParam.subCompanyId}
AND eu.real_name LIKE CONCAT('%','${maps.incomeQueryParam.salesmanName}','%' )
AND ( (#{maps.incomeQueryParam.startTime}>esod.statement_start_time AND esod.statement_end_time>= #{maps.incomeQueryParam.startTime}) OR (#{maps.incomeQueryParam.endTime}>esod.statement_start_time AND esod.statement_end_time>= #{maps.incomeQueryParam.endTime}) OR (esod.statement_start_time >= #{maps.incomeQueryParam.startTime} AND #{maps.incomeQueryParam.endTime} > esod.statement_end_time) )

SELECT, eo3.order_seller_id salesman_id, eo3.order_sub_company_id sub_company_id, (CASE WHEN esod.order_item_type = 1 THEN eop.rent_length_type WHEN esod.order_item_type = 2 THEN eom.rent_length_type end) rent_length_type, (CASE WHEN esod.order_item_type = 1 THEN eop.rent_type WHEN esod.order_item_type = 2 THEN eom.rent_type end) rent_type, (CASE WHEN esod.order_item_type = 1 THEN eop.product_count WHEN esod.order_item_type = 2 THEN eom.material_count end) goods_count, (CASE WHEN esod.order_item_type = 1 THEN eop.product_unit_amount WHEN esod.order_item_type = 2 THEN eom.material_unit_amount end) goods_unit_amount FROM erp_statement_order_detail esod LEFT JOIN erp_order_product eop ON eop.id = esod.order_item_refer_id AND eop.data_status=1 AND esod.order_item_type = 1 LEFT JOIN erp_order_material eom ON eom.id = esod.order_item_refer_id AND eom.data_status=1 AND esod.order_item_type = 2 LEFT JOIN erp_order eo1 ON eop.order_id = eo1.id AND eo1.data_status=1 LEFT JOIN erp_order eo2 ON eom.order_id = eo2.id AND eo2.data_status=1 LEFT JOIN erp_order eo3 ON esod.order_id = eo3.id AND eo3.data_status=1 LEFT JOIN erp_user eu ON eu.id=eo3.order_seller_id WHERE esod.data_status = 1 AND esod.order_type = 1 AND esod.statement_detail_status=8
AND (CASE WHEN esod.order_item_type = 1 THEN eop.rent_length_type WHEN esod.order_item_type = 2 THEN eom.rent_length_type end)=#{maps.rentInfoQueryParam.rentLengthType}
AND eo3.order_sub_company_id = #{maps.rentInfoQueryParam.subCompanyId}
AND eu.real_name LIKE CONCAT('%','${maps.rentInfoQueryParam.salesmanName}','%' )
AND esod.statement_expect_pay_time>=#{maps.rentInfoQueryParam.startTime} AND #{maps.rentInfoQueryParam.endTime}>esod.statement_expect_pay_time

