mapper.select:
SELECT  FROM erp_transfer_order_material_bulk etomb 


		AND etomb.data_status = 1


LIMIT #{maps.start},#{maps.pageSize}

SELECT count(id) FROM erp_transfer_order_material_bulk etomb


		and etomb.transfer_order_material_id = #{maps.transferOrderMaterialBulkQueryParam.transferOrderMaterialId, jdbcType=INTEGER}


		and etomb.data_status = 1

mapper.update:
update erp_transfer_order_material_bulk
SET

	transfer_order_id =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.transferOrderId,jdbcType=INTEGER}



	transfer_order_material_id =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.transferOrderMaterialId,jdbcType=INTEGER}



	bulk_material_no =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.bulkMaterialNo,jdbcType=VARCHAR}



	data_status =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.dataStatus,jdbcType=INTEGER}



	remark =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.transferOrderMaterialId,jdbcType=VARCHAR}



	create_time =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.createTime,jdbcType=TIMESTAMP}



	create_user =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.createUser,jdbcType=VARCHAR}



	update_time =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.updateTime,jdbcType=TIMESTAMP}



	update_user =

		when #{transferOrderMaterialBulkDO.id,jdbcType=INTEGER} then #{transferOrderMaterialBulkDO.updateUser,jdbcType=VARCHAR}


WHERE id in

	#{transferOrderMaterialBulkDO.id,jdbcType=INTEGER}
