mapper.select:
		select count(ewl.id) from erp_workflow_link ewl


                and ewl.workflow_type = #{maps.workflowQueryParam.workflowType, jdbcType=INTEGER}


				and ewl.workflow_refer_no like CONCAT('%','${maps.workflowQueryParam.workflowReferNo}','%' )


				and ewl.current_verify_status = #{maps.workflowQueryParam.verifyStatus, jdbcType=INTEGER}


				and ewl.current_verify_user = #{maps.workflowQueryParam.currentVerifyUser, jdbcType=INTEGER}


				and ewl.commit_user = #{maps.workflowQueryParam.commitUserId, jdbcType=INTEGER}


				 AND ewl.create_time >= #{maps.workflowQueryParam.createStartTime} 


				 AND ewl.create_time <= #{maps.workflowQueryParam.createEndTime} 


				and ewl.data_status = 1

select link.*,
eu.real_name as detail_verify_user_name,ewnc.workflow_node_name as detail_workflow_current_node_name,ewnp.workflow_node_name as detail_workflow_previous_node_name,ewnn.workflow_node_name as detail_workflow_next_node_name
from ( select
eu.real_name as current_verify_user_name, euc.real_name as commit_user_name, ewn.workflow_node_name as workflow_current_node_name, CASE WHEN ewl.workflow_step = ewl.workflow_last_step THEN true ELSE false END AS is_last_step, ewt.workflow_name AS workflow_template_name
from erp_workflow_link ewl INNER JOIN erp_workflow_template ewt ON ewt.id = ewl.workflow_template_id LEFT JOIN erp_user eu ON eu.id = ewl.current_verify_user LEFT JOIN erp_user euc ON euc.id = ewl.commit_user LEFT JOIN erp_workflow_node ewn ON ewn.id = ewl.workflow_current_node_id
and ewl.workflow_type = #{maps.workflowQueryParam.workflowType, jdbcType=INTEGER}
and ewl.workflow_refer_no like CONCAT('%','${maps.workflowQueryParam.workflowReferNo}','%' )
and ewl.current_verify_status = #{maps.workflowQueryParam.verifyStatus, jdbcType=INTEGER}
and ewl.current_verify_user = #{maps.workflowQueryParam.currentVerifyUser, jdbcType=INTEGER}
and ewl.commit_user = #{maps.workflowQueryParam.commitUserId, jdbcType=INTEGER}
and ewl.data_status = 1
order by ewl.id DESC LIMIT #{maps.start},#{maps.pageSize} ) link left join erp_workflow_link_detail ewld on ewld.workflow_link_id = link.id and ewld.data_status = 1 LEFT JOIN erp_user eu ON eu.id = ewld.verify_user LEFT JOIN erp_workflow_node ewnc on ewnc.id = ewld.workflow_current_node_id LEFT JOIN erp_workflow_node ewnp on ewnp.id = ewld.workflow_previous_node_id LEFT JOIN erp_workflow_node ewnn on ewnn.id = ewld.workflow_next_node_id order by link.id DESC,ewld.id DESC
