SELECT COUNT(DISTINCT (esod.order_item_refer_id)) total_count, SUM(CASE WHEN esod.statement_detail_type = 2 THEN esod.statement_detail_rent_deposit_paid_amount ELSE 0 END ) total_rent_deposit, SUM(CASE WHEN esod.statement_detail_type = 2 THEN esod.statement_detail_deposit_paid_amount ELSE 0 END ) total_deposit, SUM(esod.statement_detail_other_paid_amount) total_other_paid, SUM(CASE WHEN esord.return_type=1 THEN esord.return_amount ELSE 0 END ) total_return_rent_deposit, SUM(CASE WHEN esord.return_type=2 THEN esord.return_amount ELSE 0 END ) total_return_deposit FROM erp_statement_order_detail esod LEFT JOIN erp_order_product eop ON eop.id = esod.order_item_refer_id AND eop.data_status=1 AND esod.order_item_type = 1 LEFT JOIN erp_order_material eom ON eom.id = esod.order_item_refer_id AND eom.data_status=1 AND esod.order_item_type = 2 LEFT JOIN erp_statement_order_return_detail esord ON esod.id = esord.statement_order_id AND esord.data_status=1 AND esod.statement_detail_type = 4 AND esord.return_time>=#{maps.incomeQueryParam.startTime} AND #{maps.incomeQueryParam.endTime}>=esord.return_time LEFT JOIN erp_customer ec ON esod.customer_id = ec.id LEFT JOIN erp_order eo1 ON eop.order_id = eo1.id AND eo1.data_status=1 LEFT JOIN erp_order eo2 ON eom.order_id = eo2.id AND eo2.data_status=1 LEFT JOIN erp_user eu ON eu.id=(CASE WHEN esod.order_item_type = 1 THEN eo1.order_seller_id WHEN esod.order_item_type = 2 THEN eo2.order_seller_id END) WHERE esod.data_status = 1 AND esod.order_type = 1
AND (CASE WHEN esod.order_item_type = 1 THEN eop.rent_length_type WHEN esod.order_item_type = 2 THEN eom.rent_length_type end) = #{maps.incomeQueryParam.rentLengthType}
AND ec.customer_name LIKE CONCAT('%','${maps.incomeQueryParam.customerName}','%' )
AND (CASE WHEN esod.order_item_type = 1 THEN eo1.order_sub_company_id WHEN esod.order_item_type = 2 THEN eo2.order_sub_company_id END) = #{maps.incomeQueryParam.subCompanyId}
AND eu.real_name LIKE CONCAT('%','${maps.incomeQueryParam.salesmanName}','%' )
AND ( esord.return_time>=#{maps.incomeQueryParam.startTime} AND #{maps.incomeQueryParam.endTime}>=esord.return_time OR ( (#{maps.incomeQueryParam.startTime}>=esod.statement_start_time AND esod.statement_end_time>= #{maps.incomeQueryParam.startTime}) OR (#{maps.incomeQueryParam.endTime}>=esod.statement_start_time AND esod.statement_end_time>= #{maps.incomeQueryParam.endTime}) OR (esod.statement_start_time >= #{maps.incomeQueryParam.startTime} AND #{maps.incomeQueryParam.endTime} >= esod.statement_end_time) ) )

