sc ON esc.id=(CASE WHEN esod.order_item_type = 1 THEN eo1.order_sub_company_id WHEN esod.order_item_type = 2 THEN eo2.order_sub_company_id END) and esc.data_status = 1 WHERE esod.data_status = 1 AND esod.order_type = 1
AND (CASE WHEN esod.order_item_type = 1 THEN eop.rent_length_type WHEN esod.order_item_type = 2 THEN eom.rent_length_type end) = #{maps.incomeQueryParam.rentLengthType}
AND ec.customer_name LIKE CONCAT('%','${maps.incomeQueryParam.customerName}','%' )
AND (CASE WHEN esod.order_item_type = 1 THEN eo1.order_sub_company_id WHEN esod.order_item_type = 2 THEN eo2.order_sub_company_id END) = #{maps.incomeQueryParam.subCompanyId}
AND eu.real_name LIKE CONCAT('%','${maps.incomeQueryParam.salesmanName}','%' )
AND ( esord.return_time>=#{maps.incomeQueryParam.startTime} AND #{maps.incomeQueryParam.endTime}>=esord.return_time OR ( (#{maps.incomeQueryParam.startTime}>=esod.statement_start_time AND esod.statement_end_time>= #{maps.incomeQueryParam.startTime}) OR (#{maps.incomeQueryParam.endTime}>=esod.statement_start_time AND esod.statement_end_time>= #{maps.incomeQueryParam.endTime}) ) ) GROUP BY rent_length_type,esod.order_item_refer_id,esod.order_item_type ORDER BY esod_create_time ASC LIMIT #{maps.start},#{maps.pageSize}

